import React, { useEffect, useState } from "react";
import sampleIcon from '../../../assets/images/default.png'
import AdminCompanyItem from "./AdminCompanyItem";
import { fetchApiData } from "../../../api/api";

const openModal = () => {
    let modal = document.getElementsByClassName('modal-company')[0];
    modal.style.display = 'block';
}

const closeModal = () => {
    let modal = document.getElementsByClassName('modal-company')[0];
    modal.style.display = 'none';
}

const AdminCustomCompany = () => {

    const [fetchState, setFetchState] = useState(true);
    const [companyData, setCompanyData] = useState([]);

    const fetchCategory = async () => {
        const response = await fetchApiData(`loadCompany`)
        setCompanyData(response.data.company)
    }

    useEffect(() => {
        setFetchState(false);
        fetchCategory()
        console.log(companyData)
    }, [fetchState])

    const [companyName, setCompanyName] = useState('');
    const [companyEmail, setCompanyEmail] = useState('');
    const [companyPassword, setCompanyPassword] = useState('');
    const [companyConfirmPassword, setCompanyConfirmPassword] = useState('');
    const [companyStatus, setCompanyStatus] = useState('active');
    const [companyLocation, setCompanyLocation] = useState('');
    const [companyDescription, setCompanyDescription] = useState('');

    return (
        <section className="admin">
            <div className="container">
                <div className='company-div'>
                    <div className="info">
                        <h1>Company</h1>
                        <div className='button-div'>
                        <button type='button' onClick={openModal} className="button">
                            <div>
                                {/* <img src='' alt='' height='15px' width='15px'/> */}
                                <span>Add Company</span>
                            </div>
                        </button>
                    </div>
                    </div>
                    <div className="company__wrapper">
                        {/* <AdminCompanyItem index={0}/>
                        <AdminCompanyItem index={1}/> */}
                        {
                            companyData.map((item, i) => (
                                <AdminCompanyItem key={i} index={i} id={item.id} name={item.name} slug={item.slug} location={item.location} image={item.image} job_count={item.job_count}/>
                            ))
                        }
                    </div>
                    {/* Job that belong to this company are here */}
                    <>
                        {/* <CompanyJob /> */}
                        {/* <JobItem title='Check' slug='a' type='full time' company='PT Tes' icon='http://localhost:8000/files/jobs/default.png'/> */}
                        {/* <FeaturedJobItem title='Check' slug='a' type='full time' company='PT Tes' icon='http://localhost:8000/files/jobs/default.png'/> */}
                    </>
                </div>
            </div>

            <div className="modal modal-company">
                <div className="modal-container">
                   <div className="photo">
                        <img src={sampleIcon} alt="sample" />
                    </div>
                    <form>
                        <div className="form">
                            <div className='form-row'>
                                <label htmlFor="name">Company Name: </label>
                                <input type="text" className='form-control' name="name" placeholder='Company Name'/>
                            </div>
                            <div className='form-row'>
                                <label htmlFor="name">Company Email: </label>
                                <input type="email" className='form-control' name="email" placeholder='Company Email'/>
                            </div>
                            <div className='form-row'>
                                <label htmlFor="name">Company Password: </label>
                                <input type="password" className='form-control' name="Company Password" placeholder='Company Email'/>
                            </div>
                            <div className='form-row'>
                                <label htmlFor="name">Confirm Password: </label>
                                <input type="password" className='form-control' name="confirm Password" placeholder='Company Email'/>
                            </div>
                            <div className='form-row'>
                                <label htmlFor="location">Company Location: </label>
                                <input type="text" className='form-control' name="location" placeholder='Company Location'/>
                            </div>
                            <div className='form-row'>
                                <label htmlFor="description">Description: </label>
                                <input type="text" className='form-control' name="description" placeholder='Company Description'/>
                            </div>
                            <div className='button-div'>
                                <button type='button' className="button">
                                    <div>
                                        {/* <img src='' alt='' height='15px' width='15px'/> */}
                                        <span>Create Company</span>
                                    </div>
                                </button>
                                <button type='button' onClick={closeModal} className="button button-cancel">
                                    <div>
                                        {/* <img src='' alt='' height='15px' width='15px'/> */}
                                        <span>Cancel</span>
                                    </div>
                                </button>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        </section>
    )
}

export default AdminCustomCompany
