import React from 'react'
import NavBar from '../user/pages/navigation/NavBar'
import CompanyItem from './CompanyItem'
import Footer from '../user/footer/Footer'
import { storeApiData } from '../../api/api'

const AdminBanItem = ( { refresh_normal, refresh_banned, user_id, profile, status } ) => {
    // console.log(company_id, user_id)
    // console.log(user_id, user_profile)
    const banUser = () => {
        const createBlacklist = async () => {
            await storeApiData(`banUser/ban/${user_id}`)
            .then((response) => {})
            .then((response) => { refresh_normal(); refresh_banned(); })
            .catch((response) => {})
        }

        createBlacklist()
    }

    const unbanUser = () => {
        const removeBlacklist = async () => {
            await storeApiData(`banUser/unban/${user_id}`)
            .then((response) => {})
            .then((response) => { refresh_banned(); refresh_normal(); })
            .catch((response) => {})
        }

        removeBlacklist()
    }

    return (
        <>
            <div className='blacklist__wrapper'>
                {/* <p>1</p> */}
                <p>{profile.fullname}</p>
                {/* Button for user & profile detail, placed before the action button */}
                {
                    status === 'normal' ? (
                        <>
                            <button className='ban-button' onClick={banUser} type='button'>Ban</button>
                        </>
                    ) : (
                        <>
                            {/* <p>{blacklist_id}</p> */}
                            <button className='ban-button' onClick={unbanUser} type='button'>Unban</button>
                        </>
                    )
                }
            </div>
        </>
    )
}

export default AdminBanItem
