import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import sampleIcon from '../../assets/images/default.png'
import { fetchApiData, storeApiData } from "../../api/api";

const toggleDropdown = (dropdownState, index) => {
    let dropdown = document.getElementsByClassName('company_applier__wrapper__card--center__dropdown')[index];
    if (dropdownState) {
        dropdown.style.display = 'none';
    }else {
        dropdown.style.display = 'block';
    }
}

const CompanyApplierItem = ({index, id, title, description, document, status, applicationDate, profile, company, job}) => {
    const [dropdownState, setDropdownState] = useState(false)
    const [doRefresh, setDoRefresh] = useState(false)
    
    const toggle = () => {
        toggleDropdown(dropdownState, index)

        setDropdownState(!dropdownState);
    }

    const showDetail = () => {
        let detail = document.getElementsByClassName('applier-detail');
    }

    return (
        <>
        <button onClick={showDetail}>
            <div className="company_applier__wrapper__card">
                <div className="company_applier__wrapper__card--center">
                    <div className="company_applier__wrapper__card--center__part-1">
                        <h1>{profile.fullname}</h1>
                        <p>{job.title}</p>
                        <Link className={status === "pending" ? "full-time" : "half-time"} to={`/job-details/${status}`}>{status}</Link>
                    </div>
                    <div className="company_applier__wrapper__card--center__part-2">
                        <i className="fa fa-caret-down" onClick={toggle}></i>
                    </div>
                    <ul className="company_applier__wrapper__card--center__dropdown">
                        <li>
                            <button type="button" className="company_applier__wrapper__card--center__dropdown_item"><i className="fa fa-pencil fa-fw"></i><span>Edit</span></button>
                        </li>
                        <li>
                            <button type="button" className="company_applier__wrapper__card--center__dropdown_item"><i className="fa fa-trash-o fa-fw"></i><span>Delete</span></button>
                        </li>
                    </ul>
                </div>
            </div>
        </button>
        <div className="applier-detail">
                <div className="modal-container">
                   <div className="photo">
                        <img src={sampleIcon} alt="sample" />
                    </div>
                </div>
            </div>
        </>
    )
}

export default CompanyApplierItem
