import React, { useEffect, useState } from 'react'
import NavBar from '../navigation/NavBar'
import Footer from '../../footer/Footer'
import { Link, useParams } from 'react-router-dom'
import Loader from '../../../../services/Loader'

const ForgotPasswordItem = () => {
    const [loader, setLoader] = useState(true);
    const [password, setPassword] = useState('')
    const [confirmPassword, setConfirmPasswrd] = useState('')
    const [errMsg, setErrMsg] = useState('');
    let url = useParams()
    console.log(url)

    const { token, hashEmail } = url

    console.log(token, hashEmail)

    const submitHandler = () => {
        
    }

    useEffect(() => {
        setTimeout(() => {
            setLoader(false);
        }, 300)
    }, []);
    return (
        <>
            {loader ? (
                <Loader />
            ) : (
                <>
                    <NavBar hero='Forgot Password' cmp='auth'/>
                    <section className='login'>
                        <div className="container">
                            <div className="auth-div">
                                <form>
                                    <div className="form">
                                        <div>
                                            <input type="password" className='form-control' name="password" placeholder='New Password'/>
                                            <input type="password" className='form-control' name="confirmPassword" placeholder='Confirm Password'/>
                                        </div>
                                        <br />
                                        <p className='auth-error'>{errMsg}</p>
                                        <button className="button" onClick={submitHandler}>
                                            <div>
                                                {/* <img src='' alt='' height='15px' width='15px'/> */}
                                                <span>Submit</span>
                                            </div>
                                        </button>
                                       <div className="forgot">
                                            <Link to='/login'>Login form here</Link>
                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </section>
                    <Footer/>
                </>
            )}
            
        </>
    )
}

export default ForgotPasswordItem
