    //         fetchData();
    //     // }, 300);
    // }, [profileData]);

    // console.log(profileData)

    // const checkProfile = async () => {
    //     await axios.get('http://localhost:8000/auth/user-profile', {
    //         headers: {
    //             Authorization: `Bearer ${localStorage.getItem('token')}`, 
