import React, { useState } from 'react'
import sampleIcon from '../../assets/images/default.png'
import CompanyJob from './CompanyJob';
import axios from 'axios';
import { fetchApiData } from '../../api/api';

const openModal = () => {
    let modal = document.getElementsByClassName('modal')[0];
    modal.style.display = 'block';
}

const closeModal = () => {
    let modal = document.getElementsByClassName('modal')[0];
    modal.style.display = 'none';
}

const CompanyItem = () => {
    //Company Profile
    const [userData, setUserData] = useState('')
    const [companyData, setCompanyData] = useState('')

    const [name, setName] = useState('')
    const [location, setLocation] = useState('')
    const [email, setEmail] = useState('')
    const [descriptin, setDescription] = useState('')

    const checkCompany = async () => {
        try {
            const response = await axios.get('http://localhost:8000/auth/user-profile', {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('token')}`, 
                },
            });
            setUserData(response.data.data);
        } catch (error) {
            console.log(error.response.data);
        }
    };

    const getCompleteCompany = async () => {
        if (userData) {
            const response = await fetchApiData(`user-complete-company/${userData.id}`);
            if (response && response.status && response.status === true) {
                setCompanyData(response.data.profile);
            }else {
                console.log(response);
            }
        }
    };

    const setProfile = () => {
        if (companyData && userData) {
            setName(companyData.name)
            setEmail(userData.email)
            setLocation(companyData.location)
            setDescription(companyData.descriptin)
            setLastEducation(profileData.last_education)
            setStatus(profileData.status)
            setDreamJob(categoryData[profileData.dream_job-1].name)
        }
    }

    //Add Job
    const [type, setType] = useState('full time');
    const [category, setCategory] = useState('');

    return (
        <section className='company'>
            <div className="container">
                <div className="configure-profile-div">
                    <div className="photo">
                        <img src={sampleIcon} alt="sample" />
                    </div>
                    <form>
                        <div className="form">
                            <div className='form-row'>
                                <label htmlFor="name">Name: </label>
                                <input type="text" className='form-control' name="name" placeholder='company name'/>
                            </div>
                            <div className='form-row'>
                                <label htmlFor="location">Location: </label>
                                <input type="text" className='form-control' name="location" placeholder='company location'/>
                            </div>
                            <div className='form-row'>
                                <label htmlFor="email">Email: </label>
                                <input type="email" className='form-control' name="email" placeholder='company email'/>
                            </div>
                            <div className='form-row'>
                                <label htmlFor="description">Description: </label>
                                <input type="text" className='form-control' name="address" placeholder='company description'/>
                            </div>
                            <div className='button-div'>
                                <button className="button">
                                    <div>
                                        {/* <img src='' alt='' height='15px' width='15px'/> */}
                                        <span>Apply Changes</span>
                                    </div>
                                </button>
                            </div>
                        </div>
                    </form>
                </div>
                <div className='job-profile-div'>
                    <div className="info">
                        <h1>Job</h1>
                        <div className='button-div'>
                        <button type='button' onClick={openModal} className="button">
                            <div>
                                {/* <img src='' alt='' height='15px' width='15px'/> */}
                                <span>Add Job</span>
                            </div>
                        </button>
                        </div>
                    </div>
                    {/* Job that belong to this company are here */}
                    <>
                        <CompanyJob />
                        {/* <JobItem title='Check' slug='a' type='full time' company='PT Tes' icon='http://localhost:8000/files/jobs/default.png'/> */}
                        {/* <FeaturedJobItem title='Check' slug='a' type='full time' company='PT Tes' icon='http://localhost:8000/files/jobs/default.png'/> */}
                    </>
                </div>
            </div>

            <div className="modal">
                <div className="modal-container">
                   <div className="photo">
                        <img src={sampleIcon} alt="sample" />
                    </div>
                    <form>
                        <div className="form">
                            <div className='form-row'>
                                <label htmlFor="title">Title: </label>
                                <input type="text" className='form-control' name="title" placeholder='Job Title'/>
                            </div>
                            <div className='form-row'>
                                <label htmlFor="tag">Tag: </label>
                                <input type="text" className='form-control' name="tag" placeholder='Job Tag'/>
                            </div>
                            <div className='form-row'>
                                <label htmlFor="count">Count: </label>
                                <input type="number" className='form-control' name="count"  placeholder='People Needed'/>
                            </div>
                            <div className='form-row'>
                                <label htmlFor="salary">Salary: </label>
                                <input type="text" className='form-control' name="salary" placeholder='Job Salary'/>
                            </div>
                            <div className='form-row'>
                                <label htmlFor="close_date">Close Date: </label>
                                <input type="date" className='form-control' name="close_date" placeholder='close date'/>
                            </div>
                            <div className='form-row'>
                                <label htmlFor="description">Description: </label>
                                <input type="text" className='form-control' name="address" placeholder='Job Description'/>
                            </div>
                            <div className='form-row'>
                                <label htmlFor="category">Category: </label>
                                <select className='form-control' value={category} onChange={(e)=>setCategory(e.target.value)}>
                                    <option value=''>Select an Option</option>
                                    {
                                        // reamData.map(item => {
                                        //     <option key={item.id} value={item.name}>{item.name}</option>
                                        // })
                                    }
                                </select>
                            </div>
                            <div className='form-row'>
                                <label htmlFor="type">Type: </label>
                                <select className='form-control' value={type} onChange={(e)=>setType(e.target.value)}>
                                    <option value='full time'>full time</option>
                                    <option value='part time'>part time</option>
                                    <option value='half time'>half time</option>
                                </select>
                            </div>
                            <div className='button-div'>
                                <button type='button' className="button">
                                    <div>
                                        {/* <img src='' alt='' height='15px' width='15px'/> */}
                                        <span>Post Job</span>
                                    </div>
                                </button>
                                <button type='button' onClick={closeModal} className="button button-cancel">
                                    <div>
                                        {/* <img src='' alt='' height='15px' width='15px'/> */}
                                        <span>Cancel</span>
                                    </div>
                                </button>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        </section>
    )
}

export default CompanyItem
