import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import sampleIcon from '../../../assets/images/default.png'
import axios from 'axios'
import { fetchApiData } from '../../../api/api'

const ProfileItem = () => {
    const [userData, setUserData] = useState('')
    const [profileData, setProfileData] = useState('')

    const [fullname, setFullname] = useState('')
    const [email, setEmail] = useState('')
    const [age, setAge] = useState(0)
    const [address, setAddress] = useState('')
    const [lastEducation, setLastEducation] = useState('')
    const [dreamJob, setDreamJob] = useState('')
    const [status, setStatus] = useState('')

    // const loginHandler = () => {
    //     function mockAPICall(id) {
    //         return new Promise((resolve, reject) => {
    //             setTimeout(() => {
    //                 resolve(`Response from API with ID ${id}`);
    //             }, 1000); // Simulate delay of 1 second
    //         });
    //     }

    //     const checkProfile = async () => {
    //         await axios.get('http://localhost:8000/auth/user-profile', {
    //             headers: {
    //                 Authorization: `Bearer ${localStorage.getItem('token')}`, 
    //             //   Authorization: `Bearer ${tokenData.token}`, 
    //             },
    //         }).then((response)=>setUserData(response.data.data))
    //         .then(console.log(userData))
    //         .catch((response)=>console.log(response.data))
    //     }

    //     const chainLoginCall = () => {
    //         mockAPICall(1)
    //         .then(response1 => {
    //             return checkProfile()
    //             // return getToken()
    //         })
    //         .then(response2 => {
    //         })
    //         .catch(error => {
    //             console.error('Error:', error);
    //         });
    //     }

    //     // localStorage.clear()

    //     // chainLoginCall()
    //     // checkProfile()
    // }

    // function mockAPICall(id) {
    //     return new Promise((resolve, reject) => {
    //         setTimeout(() => {
    //             resolve(`Response from API with ID ${id}`);
    //         }, 1000); // Simulate delay of 1 second
    //     });
    // }

    // const checkProfile = async () => {
    //     await axios.get('http://localhost:8000/auth/user-profile', {
    //         headers: {
    //             Authorization: `Bearer ${localStorage.getItem('token')}`, 
    //         //   Authorization: `Bearer ${tokenData.token}`, 
    //         },
    //     }).then((response)=>setUserData(response.data.data))
    //     .then(console.log(userData))
    //     .catch((response)=>console.log(response.data))
    // }

    // const chainLoginCall = () => {
    //     mockAPICall(1)
    //     .then(response1 => {
    //         return checkProfile()
    //         // return getToken()
    //     })
    //     .then(response2 => {

    //     })
    //     .catch(error => {
    //         console.error('Error:', error);
    //     });
    // }

    // checkProfile()
    // chainLoginCall(1)

    // loginHandler()

    useEffect(() => {
        const checkProfile = async () => {
            try {
                const response = await axios.get('http://localhost:8000/auth/user-profile', {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('token')}`, 
                    },
                });
                setUserData(response.data.data); // Set userData after successful response
            } catch (error) {
                console.log(error.response.data); // Handle error
            }
        };
    
        if (!userData) { // Check if userData is null before calling checkProfile
            checkProfile();
        }
    }, [userData]); // Run effect whenever userData changes
    
    const getCompleteProfile = async () => {
        if (userData) { // Check if userData is not null before setting email
            // setFullname(userData.name);
            // setEmail(userData.email);

            const response = await fetchApiData(`user-complete-profile/${11}`);
            if (response && response.status && response.status === true) {
                setProfileData(response.data.profile);
            }else {
                console.log(response);
            }
        }
    };

    const setProfile = () => {
        if (profileData) {
            setFullname(profileData.fullname)
            setEmail(userData.email)
            setAge(profileData.age)
            setAddress(profileData.address)
            setLastEducation(profileData.last_education)
            // setStatus(profileData.status)
            // setStatus('employed')
        }
    }
    
    useEffect(() => {
        getCompleteProfile(); // Call setProfile whenever userData changes
    }, [userData]);

    useEffect(() => {
        setProfile()
    }, [profileData])

    console.log(profileData)
    console.log(userData)

    // useEffect(() => {
    //     // setTimeout(() => {
    //         const fetchData = async() => {
    //             const response = await fetchApiData(`user-complete-profile/${userData.id}`);
    //             if (response && response.status && response.status === true) {
    //                 setProfileData(response.data);
    //             }else {
    //                 console.log(response);
    //             }
    //             // setLoader(false);
    //         };
    //         fetchData();
    //     // }, 300);
    // }, [profileData]);

    // console.log(profileData)

    // const checkProfile = async () => {
    //     await axios.get('http://localhost:8000/auth/user-profile', {
    //         headers: {
    //             Authorization: `Bearer ${localStorage.getItem('token')}`, 
    //         //   Authorization: `Bearer ${tokenData.token}`, 
    //         },
    //     }).then((response)=>setUserData(response.data.data))
    //     .then(console.log(userData))
    //     .catch((response)=>console.log(response.data))
    // }

    // const setProfile = () => {
    //     if (userData) {
    //         setEmail(userData.email)
    //     }
    // }

    // checkProfile()

    // setProfile()

    return (
        <section className='profile'>
            <div className="container">
                <div className="configure-div">
                    <div className="photo">
                        <img src={sampleIcon} alt="sample" />
                    </div>
                    <form>
                        <div className="form">
                            <div className='form-row'>
                                <label htmlFor="fullname">Fullname: </label>
                                <input type="text" className='form-control' name="fullname" placeholder='fullname' value={fullname} onChange={(e) => setFullname(e.target.value)} />
                            </div>
                            <div className='form-row'>
                                <label htmlFor="email">Email: </label>
                                <input type="email" className='form-control' name="email" placeholder='user email' value={email} onChange={(e) => setEmail(e.target.value)}/>
                            </div>
                            <div className='form-row'>
                                <label htmlFor="age">Age: </label>
                                <input type="number" className='form-control' name="age" placeholder='user age' value={age} onChange={(e) => setAge(e.target.value)}/>
                            </div>
                            <div className='form-row'>
                                <label htmlFor="address">Address: </label>
                                <input type="text" className='form-control' name="address" placeholder='user address' value={address} onChange={(e) => setAddress(e.target.value)}/>
                            </div>
                            <div className='form-row'>
                                <label htmlFor="lastEducation">Last Education: </label>
                                <select className='form-control' value={lastEducation} onChange={(e)=>setLastEducation(e.target.value)}>
                                    <option value=''>Select an option</option>
                                    <option value='S3'>S3</option>
                                    <option value='S2'>S2</option>
                                    <option value='S1'>S1</option>
                                    <option value='SMA/Sederajat'>SMA/Sederajat</option>
                                    <option value='SMP/Sederajat'>SMP/Sederajat</option>
                                    <option value='SD/Sederajat'>SD/Sederajat</option>
                                </select>
                            </div>
                            <div className='form-row'>
                                <label htmlFor="dreamJob">Dream Job: </label>
                                <select className='form-control' value={dreamJob} onChange={(e)=>setDreamJob(e.target.value)}>
                                    <option value=''>Select an Option</option>
                                    {
                                        // reamData.map(item => {
                                        //     <option key={item.id} value={item.name}>{item.name}</option>
                                        // })
                                    }
                                </select>
                            </div>
                            <div className='form-row'>
                                <label htmlFor="status">Status: </label>
                                <select className='form-control' value={status} onChange={(e)=>setStatus(e.target.value)}>
                                    <option value='unemploye'>Unemploye</option>
                                    <option value='employed'>Employed</option>
