import React from 'react'
import NavBar from '../user/pages/navigation/NavBar'
import CompanyItem from './CompanyItem'
import Footer from '../user/footer/Footer'

const CompanyBlacklisItem = ( { company_id, user_id, profile, status } ) => {
    // console.log(company_id, user_id)
    // console.log(user_id, user_profile)
    return (
        <>
            <div className='blacklist__wrapper'>
                <p>{user_id}</p>
                <p>{company_id}</p>
                <p>{profile.fullname}</p>
                {
                    status === 'normal' ? (
                        <>
                            <button type='button'>Blacklist</button>
                        </>
                    ) : (
                        <>
                            <button type='button'>Unblacklist</button>
                        </>
                    )
                }
            </div>
        </>
    )
}

export default CompanyBlacklisItem
