import React, { useEffect, useState } from 'react'
import Loader from '../../../services/Loader';
import NavBar from './navigation/NavBar';
import JobItemSection from './JobItemSection';
import Footer from '../footer/Footer';
import { fetchApiData } from '../../../api/api';

const AllJobs = () => {
    const [counter, setCounter] = useState(6)
    const [loader, setLoader] = useState(true);
    const [jobs, setJobs] = useState([]);

    const handleClick = () => {
        setCounter(counter + 6);
        
        const fetchData = async () => {
            const response = await fetchApiData(`home/browse/${counter}`);
            if (response.status===true) {
                setJobs(response.data);
            }else {
                console.log(response);
            }
        }
        fetchData();
    }

    // let count = 6;
    // const [loader, setLoader] = useState(true);
    // const [jobs, setJobs] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            const response = await fetchApiData(`home/browse/${counter}`);
            if (response.status===true) {
                setJobs(response.data);
            }else {
                console.log(response);
            }
        }
        fetchData();
        fetchCategory()
        setTimeout(() => {
            setLoader(false);
        }, 700)
    }, [counter]);

    const [categoryData, setCategoryData] = useState([])

    const fetchCategory = async () => {
        const categoryResponse = await fetchApiData(`loadCategory`)
        setCategoryData(categoryResponse.data.category)
    }

    // console.log(categoryData)

    const [title, setTitle] = useState('')
    const [category, setCategory] = useState('')
    const [location, setLocation] = useState('')
    const [type, setType] = useState('')

    return (
        <>
            {/* {(loader && <Loader/>) || (
                <>
                    <NavBar hero='jobs' cmp="jobs" />
                    <JobItemSection jobs={jobs.job}/>
                    <div className="load-data">
                        <button className='button' onClick={
                            ++count
                        }>Browse More</button>
                    </div>
                    <Footer />
                </>
            )} */}
            {
                (loader && <Loader /> || (
                    <>
                        <NavBar hero='jobs' cmp="jobs" />
                        <div className='searchContainer'>
                            <div className='searchRow'>
                                <input type='text' name='title' className='search' placeholder='title...'/>
                            </div>
                            <div className="searchRow">
                                
                            </div>
                            {/* <input type='text' name='search' className='search' placeholder='search...'/> */}
                            <select className='form-control' value={category} onChange={(e)=>setCategory(e.target.value)}>
                                <option value=''>Select an Option</option>
                                    {
                                        categoryData && categoryData.map((item, i) => (
                                            <option key={item.id} value={item.name}>{item.name}</option>
                                        ))
                                    }
                                    </select>
                            <input type='text' name='location' className='search' placeholder='location...'/>
                            {/* <input type='text' name='search' className='search' placeholder='search...'/> */}
                            <select className='form-control' value={type} onChange={(e)=>setType(e.target.value)}>
                                        <option value=''>Select an Option</option>
                                        <option value='full time'>Full Time</option>
                                        <option value='part time'>Part Time</option>
                                    </select>
                            <button type='button'>Search</button>
                        </div>
                        <JobItemSection jobs={jobs.job}/>
                        <div className="load-data">
                            <button className='button' onClick={handleClick}>Browse More</button>
                        </div>
                        <Footer />
                    </>
                ))
            }
        </>
    );
};

export default AllJobs
