import React from 'react'
import Search from '../../header/Search'

const NavHero = ({ hero }) => {
    return (
        <div className='hero_section'>
            <>
                {
                    hero === 'jobs' || hero === 'search' ? <><div className='title'><h1>Find Job</h1></div><div className='search_nav'>
                        <Search />    
                    </div></> : hero === 'contact' || hero === 'Login' || hero === 'Sign Up' || hero === 'Forgot Password' ? <h1 className='title'>{hero}</h1> : <></>
                }
            </>
        </div>
    )
}

export default NavHero
