import React, { useEffect, useState } from 'react'
import NavBar from '../navigation/NavBar'
import Footer from '../../footer/Footer'
import { Link } from 'react-router-dom'
import Loader from '../../../../services/Loader'
import axios from 'axios'

const OTPAuth = () => {
    const [loader, setLoader] = useState(true);

    const [user, setUser] = useState('')

    const checkProfile = async () => {
        try {
            const response = await axios.get('http://localhost:8000/auth/user-profile', {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('token')}`, 
                },
            });
            setUser(response.data.data);
        } catch (error) {
            console.log(error.response.data);
        }
    };

    const checkVerify = () => {
        if (user.verify === 1) {
            const role = localStorage.getItem('role')
            const slug = localStorage.getItem('slug')
            let realRole = null

            if (role == 'job seeker') {
                realRole = 'user'
            }

            window.location = `/${realRole}/${slug}`
        } 
    }

    useEffect(() => {
        if (!user) {
            checkProfile()
        }
    }, [])

    useEffect(() => {
        checkVerify()
    }, [user])

    useEffect(() => {
        setTimeout(() => {
            setLoader(false);
        }, 300)
    }, []);
    return (
        <>
            {loader ? (
                <Loader />
            ) : (
                <>
                    <NavBar hero='OTP Auth' cmp='auth'/>
                    <section className='login'>
                        <div className="container">
                            <div className="auth-div">
                                <form>
                                    <div className="form">
                                        <h1>Please Verify your account by email First</h1>
                                        <div>
                                            <input type="email" className='form-control' name="email" placeholder='user email'/>
                                        </div>
                                        <button className="button">
                                            <div>
                                                {/* <img src='' alt='' height='15px' width='15px'/> */}
                                                <span>Submit</span>
                                            </div>
                                        </button>
                                       <div className="forgot">
                                            <Link to='/login'>Login form here</Link>
                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </section>
                    <Footer/>
                </>
            )}
            
        </>
    )
}

export default OTPAuth
